<template>
  <div class="content-button">
    <a class="btn" href="" :style="styles" >{{text}}</a> 
  </div>
</template>

<script>
export default {
  props: [ 'text', 'color', 'textColor' ],
  computed : {
  styles () {
    return `background:${this.color}; color:${this.textColor}`
  }
}
}
</script>

<style scoped>
.btn {
  padding: 15px 40px;
  background: #25DAC5;
  border-radius: 40px;
  color: #ffffff;
  font-weight: bold;
  font-size: 17px;
  text-align: center;
}
</style>