<template>
  <div class="wrapper-categories" :style="Settings">
    <div class="container">
      <nav>
        <ul class="list-categories">
          <li
            class="tab"
            v-for="(item, index) in setting.data"
            :key="index"
            @click="selected(item)"
            :class="{ tab: true, selected: select == item }"
          >
            {{ item }}
          </li>
        </ul>
      </nav>
      <div v-if="productsData.length" class="grid-products">
        <div
          v-for="product in productsData.slice(0, 4)"
          :key="product.id"
          class="content-products"
        >
          <KoProductCard3
            :product="product"
            data-aos="fade-zoom-in"
            data-aos-easing="ease-in-back"
            data-aos-delay="100"
            >></KoProductCard3
          >
        </div>
      </div>
      <div v-else class="grid-products">
        <div class="content-products">
          <KoProductCard3
            :product="products"
            data-aos="fade-zoom-in"
            data-aos-easing="ease-in-back"
            data-aos-delay="100"
          ></KoProductCard3>
        </div>
      </div>
      <router-link to="/productos">
        <button ref="colorBtn" class="button">Ver más</button>
      </router-link>
    </div>
  </div>
</template>

<script>
import KoProductCard3 from "../_productcard/Ko-ProductCard-3";
export default {
  components: {
    KoProductCard3,
  },
  name: "Ko-Content-2",
  props: {
    dataStore: Object,
    Settings: Object,
    setting: {
      type: Object,
      default: function() {
        return {
          data: "",
          // data: ["Tecnología", "Moda", "Juguetería", "Gamer"]
        };
      },
    },
  },
  created() {
    if (this.setting.data.length) {
      this.select = this.setting.data[0];
    } else {
      this.getProductsPrev();
    }
  },
  mounted() {
    this.setBackgroungHover();
  },
  data() {
    return {
      select: "",
      products: [],
    };
  },
  computed: {
    productsData() {
      return this.$store.state.productsData.filter(
        (product) => product.categoria == this.select
      );
    },
    categories() {
      return this.dataStore.categorias;
    },
    colorCSS() {
      return this.Settings["--color_background_btn"];
    },
  },
  methods: {
    selected(name) {
      this.select = name;
    },
    getProductsPrev() {
      this.setting.data = ["Prueba1", "Prueba2", "Prueba3", "Prueba4"];
      // this.setting.data = ["Moda", "Tecnología", "Juguetería", "Gamer"];
      this.products = {
        categoria: "Prueba1",
        foto_cloudinary:
          "https://vignette.wikia.nocookie.net/la-bitacora-del-capitan/images/6/67/Not_found.png/revision/latest?cb=20190509042801&path-prefix=es",
        nombre: "Producto de prueda",
        precio: 29999,
      };
    },
    setBackgroungHover() {
      let color = getComputedStyle(this.$refs.colorBtn).getPropertyValue(
        "--color_background_btn"
      );
      let colorArray = color.split(",");
      let colorInt = parseInt(colorArray[2]);
      if (colorInt > 75) {
        colorArray[2] = `${parseInt(colorArray[2]) - 15}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      } else {
        colorArray[2] = `${parseInt(colorArray[2]) + 15}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      }
    },
  },
  watch: {
    "setting.data": function(value) {
      this.select = value[0];
    },
    colorCSS(value) {
      let colorArray = value.split(",");
      let colorInt = parseInt(colorArray[2]);
      if (colorInt > 75) {
        colorArray[2] = `${parseInt(colorArray[2]) - 20}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      } else {
        colorArray[2] = `${parseInt(colorArray[2]) + 20}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      }
    },
  },
};
</script>

<style scoped>
div.wrapper-categories {
  --btnhover: #000000;
}
.wrapper-categories {
  display: flex;
  width: 100%;
  background-color: var(--background_color_1);
  justify-content: center;
  align-items: center;
  box-sizing: border-box;
}
.container {
  display: flex;
  width: 100%;
  max-width: 1300px;
  flex-direction: row;
  align-items: center;
  justify-content: space-around;
  padding: 30px 20px;
  flex-direction: column;
}
nav {
  max-width: 700px;
  width: 100%;
  margin: 0 auto;
}
.list-categories {
  height: 100px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
}
.grid-products {
  display: grid;
  width: 100%;
  margin: 30px auto;
  grid-template-columns: repeat(4, minmax(250px, 1fr));
  grid-gap: 20px;
  grid-auto-flow: column;
  padding: 0 20px;
  justify-content: center;
}
.tab {
  background-color: #fff;
  padding: 12px 20px;
  text-transform: uppercase;
  letter-spacing: 1.5;
  font-size: 16px;
  outline: none;
  cursor: pointer;
  color: var(--color_text);
  margin: 0 auto;
  flex: 1 0 auto;
  text-align: center;
  margin: 0.2rem;
  background: transparent;
  transition: all 200ms ease-in;
}
.tab:hover {
  color: var(--color_hover_text);
  border-bottom: 1.4px solid var(--color_border);
}
.selected {
  border-bottom: 1.4px solid var(--color_border);
  transition: all 200ms ease-in;
}
.content-products {
  border-radius: 10px;
}
.content-products:hover,
.content-products:focus {
  box-shadow: 0px 0px 2px 1px var(--color_background_hover);
  border-radius: 10px;
}
.button {
  border-radius: var(--radius_btn);
  color: var(--color_background_btn);
  border: solid 2px var(--color_background_btn);
  background-color: transparent;
  padding: 8px 30px;
  width: 100%;
  font-size: 14px;
  font-weight: bold;
  cursor: pointer;
  margin-top: 10px;
  transition: all 200ms ease-in;
}
.button:hover {
  color: var(--btnhover);
  border: solid 2px var(--btnhover);
  background-color: transparent;
}
@media (max-width: 1660px) {
  .grid-products .content-products:nth-child(5) {
    display: none;
  }
  .grid-products {
    grid-template-columns: repeat(4, minmax(300px, 300px));
    justify-content: center;
  }
}
@media (max-width: 1310px) {
  .grid-products .content-products:nth-child(4) {
    display: none;
  }
  .grid-products {
    grid-template-columns: repeat(3, minmax(250px, 300px));
  }
}
@media (max-width: 810px) {
  .grid-products .content-products:nth-child(3) {
    display: none;
  }
  .grid-products {
    grid-template-columns: repeat(2, minmax(145px, 300px));
  }
}
@media (max-width: 620px) {
  .grid-categories {
    margin-top: 10px;
  }
  .tab {
    font-size: 14px;
  }
  nav {
    max-width: 550px;
  }
  .grid-products {
    grid-gap: 10px;
    padding: 0 10px;
  }
}
@media (max-width: 500px) {
  .grid-products {
    grid-template-columns: repeat(auto-fill, minmax(145px, 300px));
    grid-auto-flow: initial;
  }
  .tab {
    font-size: 12px;
  }
}
</style>
