    <div class="contenedor">
      <!-- contenedor izquierdo -->
      <div class="content-left">
        <div class="content-banner-img">
          <div class="container">
            <div class="info-content">
              <div class="container-info">
                <div class="container-name">
                  <div class="category-product-content">
                    <p class="category-product">
                      {{
                        productInfo.detalle.categoria_producto
                          .nombre_categoria_producto
                      }}
                    </p>
                  </div>
                  <div class="name-product-content">
                    <p class="name-product">{{ productInfo.detalle.nombre }}</p>
                  </div>
                  <div class="price-product-content">
                    <p class="price-product">
                      ${{ productInfo.combinaciones.combinaciones["precio"] }}
                    </p>
                  </div>
                  <div
                    class="description-product-content"
                    v-if="productInfo.info.descripcion"
                  >
                    <p
                      class="description-product"
                      v-if="productInfo.info.descripcion.length > 130"
                      v-html="
                        `${productInfo.info.descripcion.slice(0, 130)}...`
                      "
                    >
                      {{ productInfo.info.descripcion }}
                    </p>
                    <p
                      class="description-product"
                      v-else
                      v-html="`${productInfo.info.descripcion.slice(0, 130)}`"
                    >
                      {{ productInfo.info.descripcion }}
                    </p>
                  </div>
                </div>
                <div class="photos-product-content">
                  <div class="photos-selected">
                    <image-cloudinary
                      :src="setMiniPhoto(productInfo.detalle.foto_cloudinary)"
                      v-on:mouseover.native="
                        selectedPhoto(productInfo.detalle.foto_cloudinary)
                      "
                      class="img"
                    />
                    <image-cloudinary
                      :src="setMiniPhoto(foto.foto_cloudinary)"
                      v-on:mouseover.native="
                        selectedPhoto(foto.foto_cloudinary)
                      "
                      v-for="(foto, itemsfoto) in productInfo.fotos"
                      class="img"
                      :key="itemsfoto"
                    />
                  </div>
                  <div class="button-content-product">
                    <button ref="colorBtn" class="btn-product">
                      Detalles del producto
                    </button>
                  </div>
                </div>
              </div>
            </div>
            <div class="img-content">
              <img
                :src="selectPhotoUrl"
                class="img-product"
                alt="Imagen producto"
              />
            </div>
          </div>
        </div>
      </div>
      <!-- contenedor derecho -->
      <div class="content-right">
        <div class="content-banner">
          <div class="container">
