<template>
  <div class="header-container" :style="currentSettingsHeader">
    <div class="wrapper-header" ref="header">
      <div class="header">
        <KoOrder :currentSettingsHeader="currentSettingsHeader" />
        <div class="header-content-logo">
          <div class="wrapper-logo">
            <img
              :src="`https://api2.komercia.co/logos/${dataStore.tienda.logo}`"
              class="header-logo"
              data-aos="fade-right"
            />
          </div>
        </div>
        <div class="header-content-items">
          <div
            v-for="(item, index) in secciones"
            :key="`${index}${item.name}`"
            class="header-buttons"
            data-aos="fade-up"
          >
            <div @click="openMenu(item.name)">
              <p class="header-text-center">{{ item.name }}</p>
              <!-- <nuxt-link :to="item.path" class="header-text-center">{{ item.name}}</nuxt-link> -->
              <!-- <div class="icon" :is="item.icon" /> -->
            </div>
          </div>
        </div>
        <div class="header-content-icon" data-aos="fade-left">
          <div
            class="header-items-icons"
            v-for="(item, index) in links"
            :key="`${index}${item.icon}`"
          >
            <div class="header-icon" v-if="item.link" :is="item.icon" />
          </div>

          <div class="header-content-cart" @click="openOrder">
            <cart-icon class="header-icon-cart" />
            <span class="num-items">{{ productsCart }}</span>
          </div>
        </div>
        <div class="header-item-menu" @click="drawer = true">
          <menu-icon class="header-icon-menu nav-bar" />
        </div>
        <div
          class="toggle-sidebar"
          v-show="drawer"
          @click="drawer = !drawer"
        ></div>
        <el-drawer
          :style="currentSettingsHeader[0]"
          :visible.sync="drawer"
          :direction="direction"
          class="responsive"
        >
          <div id="sidebar">
            <div class="sidebar-container">
              <div class="header-content-logo-navbar">
                <div class="wrapper-logo">
                  <img
                    :src="`https://api2.komercia.co/logos/${dataStore.tienda.logo}`"
                    class="header-logo"
                    data-aos="fade-right"
                  />
                </div>
                <span @click="drawer = false">
                  <window-close-icon class="header-icon-menu" />
                </span>
              </div>
              <ul>
                <li>
                  <div v-if="currentViews.length">
                    <div
                      v-for="(item, index) in currentViews"
                      :key="`${index}${item.name}`"
                      data-aos="fade-left"
                    >
                      <li @click="openMenu(item.name)">
                        <nuxt-link :to="item.path">{{ item.name }}</nuxt-link>
                      </li>
                    </div>
                  </div>
                </li>
              </ul>
            </div>
          </div>
        </el-drawer>
      </div>
    </div>
    <div class="menu-container" :class="showMenu ? 'animated' : 'hidden'">
      <div id="menu-collapse">
        <div class="menu-grid">
          <div
            class="container-category"
            v-for="categoria in categorias"
            :key="categoria.id"
          >
            <ul class="name-category">
              <li>
                <p>{{ categoria.nombre_categoria_producto }}</p>
              </li>
              <ul class="subcategoria">
                <template v-for="subcategory in subcategories">
                  <li v-if="subcategory.categoria == categoria.id">
                    {{ subcategory.nombre_subcategoria }}
                  </li>
                </template>
              </ul>
            </ul>
          </div>
        </div>
        <div class="product-img-container">
          <div class="card-container">
            <div class="img-logo" v-if="product[0]">
              <img :src="product[0].foto_cloudinary" class="logo" />
            </div>
            <div class="btn-container">
              <button class="btn-buy">Comprar</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import KoOrder from '../_order/order1'

export default {
  components: {
    KoOrder,
  },
  name: 'Ko-Header-1',
  props: {
    dataStore: Object,
    currentSettingsHeader: Object,
  },
  mounted() {
    this.toggle = true
    this.getViews()
  },
  data() {
    return {
      toggle: false,
      indexCategory: 0,
      drawer: false,
      direction: 'rtl',
      showMenu: false,
      links: [
        {
          nombre: 'Facebook',
          icon: 'facebook-icon',
          link: this.dataStore.tienda.red_facebook,
        },
        {
          nombre: 'Twitter',
          icon: 'twitter-icon',
          link: this.dataStore.tienda.red_twitter,
        },
        {
          nombre: 'Instagram',
          icon: 'instagram-icon',
          link: this.dataStore.tienda.red_instagram,
        },
        {
          nombre: 'Youtube',
          icon: 'youtube-icon',
          link: this.dataStore.tienda.red_youtube,
        },
      ],
      secciones: [
        {
          name: 'Inicio',
          path: '/',
        },
        {
          name: 'Productos',
          path: '/productos',
        },
        {
          name: 'Carrito',
          path: '/cart',
        },
        {
          name: 'Contacto',
          path: '/contacto',
        },
      ],
      cat: [],
      suma: '',
      currentViews: [],
    }
  },
  computed: {
    productsCart() {
      return this.$store.state.productsCart.length
    },
    categorias() {
      return this.dataStore.categorias
    },
    subcategories() {
      return this.dataStore.subcategorias
    },
    product() {
      return this.dataStore.productos
    },
    topHeader() {
      return this.$refs.header.offsetTop
    },
    heightHeader() {
      return this.$refs.header.offsetHeight
    },
    views() {
      return this.$store.state.views
    },
    pagination() {
      return this.$store.state.pagination
    },
  },
  methods: {
    openOrder() {
      this.$store.state.openOrder = true
    },
    openMenu(nombre) {
      var intro = document.getElementById('menu-collapse')
      if (nombre == 'Productos') {
        this.showMenu = !this.showMenu
      }
      if (this.showMenu == false) {
        intro.style.display = 'none'
      } else {
        intro.style.display = 'flex'
      }
    },
    getViews() {
      this.currentViews = []
      this.views.map((item, index) => {
        if (this.views[index].id == this.pagination[index]) {
          this.currentViews.push(item)
        }
      })
    },
  },
  watch: {
    'dataStore.tienda'() {
      this.links[0].link = this.dataStore.tienda.red_facebook
      this.links[1].link = this.dataStore.tienda.red_twitter
      this.links[2].link = this.dataStore.tienda.red_instagram
      this.links[3].link = this.dataStore.tienda.red_youtube
    },
    '$refs.header'() {
      this.suma = this.topHeader + this.heightHeader
    },
    pagination() {
      this.getViews()
    },
  },
}
</script>

<style scoped>
.header-container {
  width: 100%;
  overflow: hidden;
}
.menu-container {
  width: 100%;
  background: var(--background_color_1);
  top: -100%;
  display: flex;
  opacity: 0;
  transition: all ease 0.6s;
  position: absolute;
  height: auto;
}
.wrapper-header {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  background: var(--background_color_1);
  flex-direction: column;
}
.header {
  display: flex;
  align-items: center;
  flex-direction: row;
  justify-content: space-between;
  width: 100%;
  max-width: 1300px;
  padding: 0 30px 0;
}
.header-content-logo {
  display: flex;
  justify-content: center;
  align-items: center;
}
#menu-collapse {
  display: none;
  flex-direction: row;
  width: 100%;
  max-width: 1300px;
  padding: 30px 30px 30px 20px;
  border-top: 1px solid #aba4a466;
  margin: 0 auto;
}
.menu-grid {
  column-count: 3;
  column-gap: 30px;
  column-fill: initial;
  width: 80%;
}
.subcategoria {
  font-weight: 400;
}
.subcategoria li:hover {
  background: var(--color_background_hover);
  color: var(--color_hover_text);
  border-radius: 10px;
}
.name-category li:hover {
  background: var(--color_background_hover);
  color: var(--color_hover_text);
  border-radius: 10px;
}
.name-category {
  font-size: 16px;
  font-weight: 600;
  color: var(--color_text);
  cursor: pointer;
  padding: 2px 0px 2px 10px;
  list-style: disc;
}
.subcategoria li:last-child {
  margin-bottom: 10px;
}
.wrapper-logo {
  max-width: var(--logo_width);
  padding-top: 10px;
  padding-bottom: 10px;
}
.header-logo {
  width: 100%;
  height: 100%;
  object-fit: contain;
  object-position: left;
}
.header-content-items {
  display: flex;
  flex: 1;
  margin-left: 10px;
  justify-content: center;
}
.header-buttons {
  display: flex;
  flex-direction: row;
  justify-content: space-around;
}
.header-text-center {
  font-size: 16px;
  font-weight: normal;
  color: var(--color_text);
  margin-right: 20px;
  cursor: pointer;
}
.header-text-center:hover {
  color: var(--color_hover_text);
}
.header-content-icon {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
}
.header-items-icons {
  display: flex;
  flex-direction: row;
  justify-content: space-around;
}
.header-icon {
  font-size: 18px;
  font-weight: normal;
  font-stretch: normal;
  font-style: normal;
  line-height: normal;
  letter-spacing: normal;
  color: var(--color_icon);
  margin-left: 7px;
  cursor: pointer;
}
.header-icon:hover {
  color: var(--color_hover_text);
}
.header-content-cart {
  display: flex;
  justify-content: center;
  align-items: center;
  box-sizing: border-box;
  width: 35px;
  height: 35px;
  border: var(--color_shopping_cart) 2px solid;
  border-radius: 50%;
  padding-bottom: 3px;
  margin-left: 20px;
  position: relative;
  cursor: pointer;
}
.num-items {
  font-size: 11px;
  position: absolute;
  right: -5px;
  top: -5px;
  color: var(--background_color_1);
  background-color: var(--color_shopping_cart);
  border: var(--color_shopping_cart) 1px;
  border-radius: 10px;
  line-height: 1;
  display: flex;
  padding: 3px;
  justify-content: center;
  align-items: center;
  font-weight: bold;
}
.header-icon-cart {
  font-size: 20px;
  color: var(--color_icon);
}
.header-icon-cart:hover {
  color: var(--color_hover_text);
}
.header-item-menu {
  display: none;
}
#sidebar {
  display: none;
}
.sidebar-container {
  position: relative;
  min-height: 100%;
  padding-right: 30px;
  padding-bottom: 30px;
  padding-left: 30px;
}
.toggle-sidebar {
  display: none;
  background-color: rgba(7, 14, 27, 0.473);
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
.sidebar-container {
  position: relative;
  min-height: 100%;
  padding-right: 30px;
  padding-bottom: 30px;
  padding-left: 30px;
}
.sidebar-container ul {
  padding-top: 10px;
  font-family: 'Poppins', sans-serif;
}
.header-content-logo-navbar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  border-bottom: 1px solid #dfe3e8;
}
.responsive {
  display: none;
}
.hidden {
  top: auto;
}
.animated {
  opacity: 1;
  top: auto;
}
.container-category {
  display: inline-block;
  padding-bottom: 10px;
  width: 100%;
}
.subcategoria li {
  padding-left: 10px;
}
.product-img-container {
  width: 30%;
  height: 100%;
}
.img-logo {
  /* width: 100%; */
  height: 265px;
  max-width: 250px;
  width: 100%;
  position: absolute;
}
.logo {
  height: 100%;
  border-radius: 10px;
  max-width: 250px;
  width: 100%;
  object-fit: cover;
}
.card-container {
  border-radius: 10px;
  box-shadow: 0 0 22px 3px #efeeeeb3;
  max-width: 250px;
  width: 100%;
  height: 265px;
  margin: 0 auto;
}
.btn-container {
  margin-top: 10px;
  position: relative;
  top: 75%;
  width: 100%;
  display: flex;
  justify-content: center;
}
.btn-buy {
  color: var(--color_text_btn);
  border-radius: var(--radius_btn);
  border: solid 2px var(--color_border_btn);
  background-color: var(--color_background_btn);
  padding: 8px 14px;
  font-size: 14px;
  width: 120px;
  font-weight: bold;
  cursor: pointer;
  margin-bottom: 20px;
  cursor: pointer;
}
.btn-buy:hover {
  color: var(--color_text_btn_hover);
  border: solid 2px var(--color_border_btn_hover);
  background-color: var(--color_background_btn_hover);
}
.card-container:hover,
.content-products:focus {
  box-shadow: 0px 0px 2px 1px var(--color_border);
  border-radius: 10px;
}
@media (max-width: 900px) {
  .product-img-container {
    display: none;
  }
  .menu-grid {
    width: 100%;
  }
}
@media (max-width: 700px) {
  .header-buttons {
    display: none;
  }
  .header-items-icons {
    display: none;
  }
  .header-item-menu {
    display: flex;
    align-items: center;
    flex-direction: row;
    justify-content: space-between;
    margin-left: 20px;
  }
  .header-icon-menu {
    font-size: 30px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: normal;
    letter-spacing: normal;
    color: var(--color_text);
  }
  .header-icon-menu > .material-design-icon__svg {
    bottom: 0em;
  }
  .nav-bar {
    width: 24px;
    height: 24px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: normal;
    letter-spacing: normal;
    color: var(--color_text);
  }
  .nav-bar > .material-design-icon__svg {
    bottom: 0px;
    width: 24px;
    height: 24px;
  }
  /* menu lateral */
  #sidebar {
    display: initial;
    position: fixed;
    max-width: 350px;
    width: 100%;
    height: 100vh;
    background: var(--background_color_1);
    right: 0px;
    top: 0;
    left: unset;
  }
  #sidebar.active {
    right: -200px;
  }
  #sidebar ul li {
    font-size: 16px;
    font-weight: normal;
    color: var(--color_text);
    list-style: none;
    margin: 20px 0px;
    text-align: unset;
  }
  .toggle-sidebar {
    display: initial;
  }
  .responsive {
    display: initial;
  }
  .menu-container {
    display: none;
  }
}
</style>
