<template>
  <div class="wrapper_newsletter" :style="Settings">
    <div class="contenedor">
      <div class="content-title">
        <p class="subtext">{{ this.Settings["--description"] }}</p>
        <p class="title">{{ this.Settings["--title_1"] }}</p>
      </div>
      <form class="content-button">
        <input
          class="input-text"
          type="email"
          v-model="email"
          placeholder="Correo electrónico"
          required
        />
        <button ref="colorBtn" class="btn" @click="submitNewsletter">Subscríbete</button>
      </form>
    </div>
  </div>
</template>

<script>
export default {
  name: "Ko-Newsletter-1",
  props: {
    dataStore: Object,
    Settings: Object
  },
  mounted() {
    this.setBackgroungHover();
  },
  data() {
    return {
      email: ""
    };
  },
  computed: {
    colorCSS() {
      return this.Settings["--color_background_btn"];
    }
  },
  methods: {
    submitNewsletter() {
      if (this.email) {
        const json = {
          email: this.email,
          tienda: this.$store.state.id
        };
        axios.post(`https://api2.komercia.co/api/tienda/suscriptor`, json);
      }
    },
    setBackgroungHover() {
      let color = getComputedStyle(this.$refs.colorBtn).getPropertyValue(
        "--color_background_btn"
      );
      let colorArray = color.split(",");
      let colorInt = parseInt(colorArray[2]);
      if (colorInt > 75) {
        colorArray[2] = `${parseInt(colorArray[2]) - 10}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      } else {
        colorArray[2] = `${parseInt(colorArray[2]) + 10}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      }
    }
  },
  watch: {
    colorCSS(value) {
      let colorArray = value.split(",");
      let colorInt = parseInt(colorArray[2]);
      if (colorInt > 75) {
        colorArray[2] = `${parseInt(colorArray[2]) - 10}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      } else {
        colorArray[2] = `${parseInt(colorArray[2]) + 10}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      }
    }
  }
};
</script>

<style scoped>
div.wrapper_newsletter {
  --btnhover: #000000;
}
.wrapper_newsletter {
  display: flex;
  width: 100%;
  background-color: var(--background_color_1);
  justify-content: center;
  align-items: center;
  box-sizing: border-box;
}
.contenedor {
  width: 100%;
  height: 100%;
  margin: 0 auto;
  max-width: 1300px;
  padding: 60px 20px 60px 20px;
}
.content-title {
  display: flex;
  justify-content: center;
  align-items: center;
  padding-bottom: 50px;
  flex-direction: column;
}
.title {
  font-size: 38px;
  font-weight: bold;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.24;
  letter-spacing: -0.4px;
  color: var(--color_text);
  text-align: center;
  width: 600px;
}
.subtext {
  text-align: center;
  color: var(--color_subtext);
  font-size: 14px;
  font-weight: bold;
}
.content-button {
  display: flex;
  padding-bottom: 10px;
  flex-direction: row;
  justify-content: center;
  width: 100%;
}
.input-text {
  font-size: 14px;
  color: var(--color_subtext);
  border: solid 2px #afafaf;
  border-radius: var(--radius_btn);
  background-color: transparent;
  padding: 12px 14px;
  width: 360px;
}
.input-text::placeholder {
  color: var(--color_subtext);
  opacity: 0.7;
}
.input-text:focus,
.input-text:active {
  outline: 0;
}
.btn {
  color: var(--color_text_btn);
  border-radius: var(--radius_btn);
  border: solid 2px var(--color_border_btn);
  background-color: var(--color_background_btn);
  padding: 8px 14px;
  font-size: 14px;
  width: 220px;
  font-weight: bold;
  cursor: pointer;
  margin-left: 20px;
  cursor: pointer;
  transition: all 200ms ease-in;
}
.btn:hover {
  background-color: var(--btnhover);
}
@media (max-width: 600px) {
  .title {
    width: 100%;
    max-width: 550px;
  }
}
@media (max-width: 500px) {
  .contenedor {
    width: 100%;
    height: 100%;
    margin: 0 auto;
    max-width: 1300px;
    padding: 40px 10px 30px 10px;
  }
  .title {
    font-size: 30px;
  }
  .subtext {
    font-size: 12px;
  }
  .input-text {
    width: 190px;
  }
  .btn {
    width: 110px;
    margin-left: 10px;
  }
}
</style>
