<template>
  <div class="separator" :style="Settings">
    <div class="contenedor">
      <div class="content-title">
        <p class="title">{{ this.Settings["--title_1"] }}</p>
      </div>
      <div class="content-text">
        <p class="text">{{ this.Settings["--description"] }}</p>
      </div>
      <div class="wrapper-footer">
        <button ref="colorBtn" class="btn">{{ this.Settings["--text_btn"] }}</button>
        <p class="footer">{{this.dataStore.tienda.nombre.toLowerCase()}}</p>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: "Ko-Separator-1",
  props: {
    dataStore: Object,
    Settings: Object
  },
  mounted() {
    this.setBackgroungHover();
  },
  computed: {
    colorCSS() {
      return this.Settings["--color_background_btn"];
    }
  },
  methods: {
    setBackgroungHover() {
      let color = getComputedStyle(this.$refs.colorBtn).getPropertyValue(
        "--color_background_btn"
      );
      let colorArray = color.split(",");
      let colorInt = parseInt(colorArray[2]);
      if (colorInt > 75) {
        colorArray[2] = `${parseInt(colorArray[2]) - 10}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      } else {
        colorArray[2] = `${parseInt(colorArray[2]) + 10}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      }
    }
  },
  watch: {
    colorCSS(value) {
      let colorArray = value.split(",");
      let colorInt = parseInt(colorArray[2]);
      if (colorInt > 75) {
        colorArray[2] = `${parseInt(colorArray[2]) - 10}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      } else {
        colorArray[2] = `${parseInt(colorArray[2]) + 10}%`;
        this.$refs.colorBtn.style.setProperty(
          "--btnhover",
          colorArray.join(",")
        );
      }
    }
  }
};
</script>

<style scoped>
div.separator {
  --btnhover: #000000;
}
.separator {
  display: flex;
  width: 100%;
  background-color: var(--background_color_1);
  justify-content: center;
  align-items: center;
  box-sizing: border-box;
}
.contenedor {
  width: 100%;
  height: 100%;
  margin: 0 auto;
  max-width: 1300px;
  padding: 50px 30px 50px;
}
.content-title {
  display: flex;
  justify-content: center;
  padding-bottom: 20px;
}
.content-text {
  display: flex;
  justify-content: center;
  padding-bottom: 30px;
}
.content-button {
  display: flex;
  justify-content: center;
  padding-bottom: 25px;
}
.title {
  font-size: 48px;
  font-weight: bold;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.24;
  letter-spacing: -0.4px;
  color: var(--color_text);
  text-align: center;
}
.text {
  max-width: 490px;
  width: 100%;
  margin: 0 auto;
  text-align: center;
  font-weight: bold;
  color: var(--color_subtext);
  font-size: 17px;
}
.wrapper-footer {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}
.btn {
  border-radius: var(--radius_btn);
  color: var(--color_text_btn);
  border-radius: var(--radius_btn);
  border: solid 2px var(--color_border_btn);
  background-color: var(--color_background_btn);
  padding: 12px 14px;
  font-size: 14px;
  width: 220px;
  font-weight: bold;
  cursor: pointer;
  transition: all 200ms ease-in;
}
.btn:hover {
  background-color: var(--btnhover);
}
.footer {
  margin-top: 10px;
  color: var(--color_subtext);
  font-size: 14px;
  text-transform: capitalize;
}
@media (max-width: 598px) {
  .text {
    max-width: 515px;
  }
  .title {
    padding-bottom: 40px;
    font-size: 45px;
  }
}
@media (max-width: 562px) {
  .text {
    max-width: 335px;
  }
}
</style>
